import React, {FormEvent, KeyboardEventHandler, useState} from "react";
import '../App.scss';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPlus} from "@fortawesome/free-solid-svg-icons";

import {IHomeController} from "../controller/HomeController";

export default function Home(props: { controller: IHomeController }) {
    const {controller} = props
    const [items, setItems] = useState(controller.getItems)

    const addItem = (event: FormEvent<HTMLFormElement> | object) => {
        if(typeof event === "object"){
            setItems(controller.addItem(event[0]))
        } else {
            const value: string = event.target.elements[0]
            value.length > 15 ? alert("Maximum 15 Character") : setItems(controller.addItem(value))
            event.target.elements[0].value = ""

        }
